<?php/** * @pluginName epoint Opencart 3.x Payment Gateway * @pluginUrl https://epoint.az/ * @varion 1.0.0 * @author Rauf ABBASZADE <rafo.abbas@gmail.com> * @authorURI: https://abbasazade.dev/ * @opencartVersion "3.0.x" */class ModelExtensionPaymentEpoint extends Model{    public function getMethod($address, $total)    {        if (! $this->config->get('payment_epoint_status')) {            return false;        }        $this->load->language('extension/payment/epoint');        return array(            'code' => 'epoint',            'title' => $this->language->get('text_title'),            'terms' => '',            'sort_order' => $this->config->get('payment_epoint_sort_order')        );    }    public function insertLog($order_id, $payment_id,  $request)    {        $this->db->query(            "INSERT INTO `" . DB_PREFIX . "epoint` SET order_id = '" . (int) $order_id . "', payment_id = '" . $payment_id . "', `request` = '".$request."', date_added = NOW()"        );    }    public function getLog($orderId)    {        $query = $this->db->query("SELECT * FROM `" . DB_PREFIX . "epoint` WHERE order_id = '" . (int) $orderId . "' LIMIT 1");        return $query->row;    }}